import { INCREMENT, DECREMENT } from "../action/actionTypes"

let initState = {
  count: 0
}

export default (state = initState, actions) => {
  switch(actions.type) {
    case INCREMENT:
      return state + 1
    case DECREMENT:
      return state - 1
      
    default:
      return state
  }
}
